mapper.select:
select bno
from board
where bphoto = #{saveFileName}

select count(*)
from board
where mid = #{mid}

select count(*)
from bookmark
where mid = #{mid}

(select bphoto, bdate from board where mid = #{mid}
union
select bphoto, bmdate from (select bno, bmdate from bookmark where mid = #{mid}) bm join board b on bm.bno = b.bno) order by bdate desc

select bno
from board
where mid=#{mid}
